#!/bin/bash
#SBATCH --time=01:00:00
#SBATCH --account=def-ioannisr
#SBATCH --mem=125G 

echo "Beginning replacement" 
sed '1,375s/##INFO=<ID=SAO,Number=1,Type=Integer,Description="Variant Allele Origin: 0 - unspecified, 1 - Germline, 2 - Somatic, 3 - Both">/##INFO=<ID=SAO,Number=.,Type=String,Description="Variant Allele Origin: 0 - unspecified, 1 - Germline, 2 - Somatic, 3 - Both">/' allSamples_v2.hg38_multianno.vcf | sed '1,375s/##INFO=<ID=RS,Number=1,Type=Integer,Description="dbSNP ID (i.e. rs number)">/##INFO=<ID=RS,Number=.,Type=String,Description="dbSNP ID (i.e. rs number)">/' | sed '1,375s/##INFO=<ID=RSPOS,Number=1,Type=Integer,Description="Chr position reported in dbSNP">/##INFO=<ID=RSPOS,Number=.,Type=String,Description="Chr position reported in dbSNP">/' | sed '1,375s/##INFO=<ID=SSR,Number=1,Type=Integer,Description="Variant Suspect Reason Codes (may be more than one value added together) 0 - unspecified, 1 - Paralog, 2 - byEST, 4 - oldAlign, 8 - Para_EST, 16 - 1kg_failed, 1024 - other">/##INFO=<ID=SSR,Number=.,Type=String,Description="Variant Suspect Reason Codes (may be more than one value added together) 0 - unspecified, 1 - Paralog, 2 - byEST, 4 - oldAlign, 8 - Para_EST, 16 - 1kg_failed, 1024 - other">/' | sed '1,375s/##INFO=<ID=WGT,Number=1,Type=Integer,Description="Weight, 00 - unmapped, 1 - weight 1, 2 - weight 2, 3 - weight 3 or more">/##INFO=<ID=WGT,Number=.,Type=String,Description="Weight, 00 - unmapped, 1 - weight 1, 2 - weight 2, 3 - weight 3 or more">/' | sed '1,375s/##INFO=<ID=dbSNPBuildID,Number=1,Type=Integer,Description="First dbSNP Build for RS">/##INFO=<ID=dbSNPBuildID,Number=.,Type=String,Description="First dbSNP Build for RS">/' | sed '1,375s/##INFO=<ID=MQ,Number=1,Type=Float,Description="RMS Mapping Quality">/##INFO=<ID=MQ,Number=.,Type=String,Description="RMS Mapping Quality">/' > allSamples.hg38_multianno.sed_replace.vcf
echo "Finished replacement" 
